mapper.select:
SELECT 蔵書.蔵書コード as "bookCollectionCode.value", 本.本ID as "book.bookId.value", 本.タイトル as "book.title.value", 本.著者 as "book.author.value", 本.本種別 as "book.bookType", '在庫中' as "bookCollectionStatus" FROM 蔵書 INNER JOIN 本 on (蔵書.本ID = 本.本ID) LEFT OUTER JOIN (SELECT 貸出.貸出ID as 貸出ID, 貸出.蔵書コード as 蔵書コード FROM 貸出 LEFT OUTER JOIN 返却 ON 貸出.貸出ID = 返却.貸出ID WHERE 返却.貸出ID IS NULL) 現在の貸出 ON 蔵書.蔵書コード = 現在の貸出.蔵書コード WHERE 本.本ID IN
#{bookId.value}
AND 現在の貸出.貸出ID IS NULL
